const Ex = require ( 'st_ex1' );
const Ini = require ( 'st_ini' );
const r_f = Ex.SetDirFile.SearchDirFile.DirMappingCloneFileToDir;
const arr_dir = r_f ( { namedir: __dirname }, dev = true );
const res = arr_dir.map ( item => {
    const insp_arr = [ item.indexOf ( '_' ), item.indexOf ( '.' ), item.indexOf ( '.md' ) ];

    if ( insp_arr[0] === 0 ) {
        // it's mySpec
        if ( dev ) console.log ( `${ item } it's mySpec` );
        return item;
    }
    if ( insp_arr[1] === -1 ) {
        // it's work directory
        if ( dev ) console.log ( `${ item } it's work directory` );
        return item;
    }
    else {
        if ( insp_arr[2] === -1 ) {
            // it's not MD file
            if ( dev ) console.log ( `${ item } it's not MD file` );
            return item;
        }
        else {
            // it' our MD file
            if ( dev ) console.log ( `${ item } it' our MD file` );
            // 1.create (__dirname + item) Dir
            // 2.create (__dirname + item)/_src
            // 3.create (__dirname + item)/www.md
            // 4.create (__dirname + item)/ReadMe.md
            return item;
        }
    }
} );
